// src/components/HowItWorks.js

import React from 'react';
import {
  CheckCircle,
  Smartphone,
  Globe,
  Zap,
  Shield,
  Users
} from 'react-feather';

const features = [
  {
    icon: <CheckCircle className="text-[#25D366] w-6 h-6" />,
    title: "100% Free Forever",
    description: "No hidden fees, no premium tiers. Completely free for job seekers.",
  },
  {
    icon: <Smartphone className="text-[#25D366] w-6 h-6" />,
    title: "No App Download",
    description: "Works directly through WhatsApp. Save your phone storage.",
  },
  {
    icon: <Globe className="text-[#25D366] w-6 h-6" />,
    title: "Made for SA Youth",
    description: "Designed specifically for South African job seekers.",
  },
  {
    icon: <Zap className="text-[#25D366] w-6 h-6" />,
    title: "Instant Alerts",
    description: "Get notified immediately when matching jobs are posted.",
  },
  {
    icon: <Shield className="text-[#25D366] w-6 h-6" />,
    title: "Privacy First",
    description: "Your data is secure. We never share without permission.",
  },
  {
    icon: <Users className="text-[#25D366] w-6 h-6" />,
    title: "Growing Network",
    description: "Join 10,000+ job seekers already finding opportunities.",
  },
];

function HowItWorks() {
  return (
    <section id="why-jobsta" className="py-20 bg-white">
      <div className="max-w-6xl mx-auto px-4">
        {/* Section Header */}
        <div className="text-center mb-12">
          <p className="text-[#25D366] font-semibold mb-2">Why Choose Jobsta</p>
          <h2 className="text-3xl md:text-4xl font-bold mb-4">Built for Your Success</h2>
          <p className="text-gray-600 text-lg max-w-2xl mx-auto">
            Everything you need to find your next opportunity â€” nothing you donâ€™t.
          </p>
        </div>

        {/* Features Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <div
              key={index}
              className="bg-gray-50 p-6 rounded-xl shadow-md hover:shadow-lg transition"
            >
              <div className="mb-4">
                {feature.icon}
              </div>
              <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
              <p className="text-gray-600 text-sm">{feature.description}</p>
            </div>
          ))}
        </div>

        {/* Special Highlight Box */}
        <div className="mt-16 bg-[#25D366]/10 p-6 md:p-10 rounded-2xl flex flex-col md:flex-row items-center gap-6">
          <div className="text-6xl">ðŸ‡¿ðŸ‡¦</div>
          <div className="text-center md:text-left">
            <h3 className="text-2xl font-bold mb-2">Proudly South African</h3>
            <p className="text-gray-700 text-sm">
              Built by South Africans, for South Africans. We understand the unique challenges 
              of our job market and are committed to creating opportunities for our youth.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default HowItWorks;
