import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";

function ContactForm() {
  const form = useRef();
  const [success, setSuccess] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_r603taj",     // Replace with your EmailJS service ID
        "template_ve1qtsr",    // Replace with your EmailJS template ID
        form.current,
        "pYyawR8zdc-6RNqsK"      // Replace with your EmailJS public key
      )
      .then(
        () => {
          setSuccess(true);
          form.current.reset();
        },
        (error) => {
          console.error("EmailJS Error:", error.text);
          setSuccess(false);
        }
      );
  };

  return (
    <section id="contact" className="py-24 bg-white text-black">
      <div className="container mx-auto max-w-xl px-4 text-center">
        <h2 className="text-3xl font-bold mb-6">Send Us a Message</h2>

        <form ref={form} onSubmit={sendEmail} className="space-y-4">
          <input
            type="text"
            name="user_name"
            placeholder="Your Name"
            required
            className="w-full p-3 border rounded-md"
          />
          <input
            type="email"
            name="user_email"
            placeholder="Your Email"
            required
            className="w-full p-3 border rounded-md"
          />
          <textarea
            name="message"
            rows="5"
            placeholder="Your Message"
            required
            className="w-full p-3 border rounded-md"
          ></textarea>

          <button
            type="submit"
            className="bg-[#25D366] text-white px-6 py-3 rounded-full font-semibold hover:bg-[#1da851] transition"
          >
            Send Message
          </button>
        </form>

        {success && (
          <p className="text-green-600 mt-4">Message sent successfully!</p>
        )}
      </div>
    </section>
  );
}

export default ContactForm;
