import React, { useState } from 'react';
import { Menu, X } from 'react-feather';

function Header() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <header className="sticky top-0 z-50 bg-white shadow-md">
      <div className="max-w-6xl mx-auto px-4 py-4 flex items-center justify-between"> 

        {/* Logo */}
        <h1 className="text-xl font-bold text-black mr-auto">Jobsta</h1> {/* ✅ Moved far left */}

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center gap-6 text-sm text-black ml-auto"> {/* ✅ Pushed right */}
          <a href="#how-it-works" className="hover:text-[#25D366]">How It Works</a>
          <a href="#why-jobsta" className="hover:text-[#25D366]">Why Jobsta</a>
          <a href="#testimonials" className="hover:text-[#25D366]">Testimonials</a>
          <a
            href="#get-started"
            className="bg-[#25D366] text-white px-4 py-2 rounded-full font-medium hover:bg-[#1da851] transition"
          >
            Get Started
          </a> {/* ✅ Styled like green button */}
        </nav>

        {/* Hamburger Icon */}
        <button onClick={toggleMenu} className="md:hidden text-black">
          {isOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Dropdown */}
      <div
        className={`md:hidden bg-white overflow-hidden transition-all duration-300 ease-in-out ${
          isOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
        }`}
      >
        <nav className="flex flex-col gap-4 text-sm text-black px-4 py-4">
          <a href="#how-it-works" onClick={toggleMenu} className="hover:text-[#25D366]">How It Works</a>
          <a href="#why-jobsta" onClick={toggleMenu} className="hover:text-[#25D366]">Why Jobsta</a>
          <a href="#testimonials" onClick={toggleMenu} className="hover:text-[#25D366]">Testimonials</a>
          <a
            href="#get-started"
            onClick={toggleMenu}
            className="bg-[#25D366] text-white px-4 py-2 rounded-full font-medium hover:bg-[#1da851] transition"
          >
            Get Started
          </a> {/* ✅ Same green button for mobile */}
        </nav>
      </div>
    </header>
  );
}

export default Header;
