import React, { useState } from 'react';
import { Menu, X } from 'react-feather'; // hamburger and close icons

function Header() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <header className="sticky top-0 z-50 bg-white shadow-md">
      <div className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <h1 className="text-xl font-bold text-black">Jobsta</h1>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center gap-8 text-sm text-black">
          <a href="#how-it-works" className="hover:text-[#25D366]">How It Works</a>
          <a href="#why-jobsta" className="hover:text-[#25D366]">Why Jobsta</a>
          <a href="#testimonials" className="hover:text-[#25D366]">Testimonials</a>
          <a href="#get-started" className="hover:text-[#25D366]">Get Started</a>
        </nav>

        {/* Hamburger Button (Mobile Only) */}
        <button onClick={toggleMenu} className="md:hidden text-black">
          {isOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Dropdown Menu */}
      {isOpen && (
        <div className="md:hidden bg-white shadow-md px-4 pb-4">
          <nav className="flex flex-col gap-4 text-sm text-black">
            <a href="#how-it-works" onClick={toggleMenu} className="hover:text-[#25D366]">How It Works</a>
            <a href="#why-jobsta" onClick={toggleMenu} className="hover:text-[#25D366]">Why Jobsta</a>
            <a href="#testimonials" onClick={toggleMenu} className="hover:text-[#25D366]">Testimonials</a>
            <a href="#get-started" onClick={toggleMenu} className="hover:text-[#25D366]">Get Started</a>
          </nav>
        </div>
      )}
    </header>
  );
}

export default Header;
