import React from 'react';
import { MessageCircle, Instagram, Twitter, Linkedin, Mail, MapPin } from "lucide-react";

export function Footer() {
  const currentYear = new Date().getFullYear();

  const socialLinks = [
    { icon: MessageCircle, href: "#", label: "WhatsApp" },
    { icon: Instagram, href: "#", label: "Instagram" },
    { icon: Twitter, href: "#", label: "Twitter" },
    { icon: Linkedin, href: "#", label: "LinkedIn" },
  ];

  return (
    <footer className="relative bg-foreground text-background overflow-hidden">
      {/* Gradient Overlay */}
      <div className="absolute inset-0 bg-gradient-to-t from-primary/10 to-transparent" />

      <div className="container relative z-10 mx-auto max-w-4xl px-4">
        {/* Centered Footer Content */}
        <div className="py-16 text-center space-y-6">
          <h3 className="text-3xl font-bold">Jobsta</h3>
          <p className="text-background/70 max-w-xl mx-auto">
            Connecting South African youth with opportunities, one WhatsApp message at a time.
          </p>

          {/* Social Icons */}
          <div className="flex justify-center gap-4 pt-2">
            {socialLinks.map((social, index) => (
              <a
                key={index}
                href={social.href}
                aria-label={social.label}
                className="p-3 rounded-lg bg-background/10 hover:bg-primary hover:text-white transition-all duration-300 hover:scale-110"
              >
                <social.icon className="w-5 h-5" />
              </a>
            ))}
          </div>
        </div>

        {/* Contact Info */}
        <div className="py-8 border-t border-background/10">
          <div className="flex flex-col md:flex-row items-center justify-between gap-4 text-sm text-background/70">
            <div className="flex items-center gap-3">
              <Mail className="w-4 h-4" />
              <span>hello@jobsta.co.za</span>
            </div>
            <div className="flex items-center gap-3">
              <MapPin className="w-4 h-4" />
              <span>South Africa</span>
            </div>
          </div>
        </div>

        {/* Bottom Bar */}
        <div className="py-6 border-t border-background/10 text-center space-y-2">
          <p className="text-sm text-background/70">
            Â© {currentYear} Jobsta.
          </p>
          <p className="text-xs text-background/50">
            We respect your privacy. No spam. Ever.
          </p>
        </div>
      </div>
    </footer>
  );
}

export default Footer;
