// src/components/Testimonials.js
import React from 'react';
import { Star } from 'react-feather'; // icon for rating stars

function Testimonials() {
  // 5 filled stars for each testimonial
  const stars = [...Array(5)];

  return (
    <section className="py-16 md:py-24 bg-gray-50">
      <div className="container mx-auto max-w-4xl px-4">
        <div className="text-center mb-12">
          <h2 className="text-2xl md:text-3xl font-bold mb-4">
            Trusted by 10,000+ young South Africans
          </h2>
        </div>

        <div className="grid md:grid-cols-3 gap-8">
          {/* Testimonial 1 */}
          <div className="p-6 bg-white rounded-lg shadow-sm space-y-4">
            <div className="flex text-[#25D366]">
              {stars.map((_, i) => (
                <Star key={i} className="h-4 w-4 fill-current" />
              ))}
            </div>
            <blockquote className="text-gray-600 text-sm">
              "Got my first job through Jobsta. So easy!"
            </blockquote>
            <p className="font-semibold text-sm">— Thabo M.</p>
          </div>

          {/* Testimonial 2 */}
          <div className="p-6 bg-white rounded-lg shadow-sm space-y-4">
            <div className="flex text-[#25D366]">
              {stars.map((_, i) => (
                <Star key={i} className="h-4 w-4 fill-current" />
              ))}
            </div>
            <blockquote className="text-gray-600 text-sm">
              "From unemployed to employed in 2 weeks!"
            </blockquote>
            <p className="font-semibold text-sm">— Nomsa K.</p>
          </div>

          {/* Testimonial 3 */}
          <div className="p-6 bg-white rounded-lg shadow-sm space-y-4">
            <div className="flex text-[#25D366]">
              {stars.map((_, i) => (
                <Star key={i} className="h-4 w-4 fill-current" />
              ))}
            </div>
            <blockquote className="text-gray-600 text-sm">
              "Simple, fast, and it actually works!"
            </blockquote>
            <p className="font-semibold text-sm">— Sipho D.</p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Testimonials;
