import { HiSparkles } from "react-icons/hi2";
import { HiOutlineArrowRight, HiOutlineChatBubbleLeft } from "react-icons/hi2";

function CTASection() {
  const whatsappLink = "https://wa.me/27820000000?text=Hi+Jobsta";

  return (
    <section className="py-24 bg-white text-black">
      <div className="container mx-auto max-w-4xl px-4 text-center space-y-12">
        {/* Icon */}
        <div className="inline-flex p-6 rounded-full bg-[#25D366]/10">
          <HiOutlineChatBubbleLeft className="w-12 h-12 text-[#25D366]" />
        </div>

        {/* Heading */}
        <div className="space-y-4">
          <h2 className="text-4xl md:text-6xl font-bold">
            Ready to Find Your{" "}
            <span className="block text-[#25D366]">Dream Job?</span>
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Join thousands of South Africans who've already found success.  
            Your next opportunity is just a WhatsApp message away.
          </p>
        </div>

        {/* CTA Button */}
        {/* CTA Button */}
<div className="flex justify-center">
  <button
    onClick={() => window.open(whatsappLink, "_blank")}
    className="bg-[#25D366] text-white px-6 py-3 rounded-full text-lg font-semibold flex items-center justify-center gap-2 hover:bg-[#1da851] transition duration-300"
  >
    <HiSparkles className="w-5 h-5" />
    Start Your Journey Today
    <HiOutlineArrowRight className="w-5 h-5" />
  </button>
</div>

        {/* Trust Badge */}
        <div className="flex flex-wrap justify-center gap-4 text-sm text-gray-500">
          <div className="flex items-center gap-2">
            <span className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
            Active Now
          </div>
          <span>•</span>
          <span>Join 10,000+ users</span>
          <span>•</span>
          <span>100% Free</span>
        </div>
      </div>
    </section>
  );
}

export default CTASection;
